css:
body {
    font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    font-size: 12px;
    padding: 20px;
    color: #333;
    background-color: #fff;
}

h3 {
    text-align: center;
    font-size: 20px;
    margin-bottom: 10px;
    font-weight: 600;
}

h5 {
    margin-top: 20px;
    font-size: 16px;
    font-weight: 600;
    color: #1e5a8c;
}

.row {
    margin-bottom: 5px;
}

.table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 10px;
}

.table th, .table td {
    border: 1px solid #ccc;
    padding: 6px 10px;
    vertical-align: middle;
}

.table thead th {
    background-color: #f2f2f2;
    font-weight: bold;
    text-align: left;
}

.table td.text-end, .table th.text-end {
    text-align: right;
}

.fw-bold {
    font-weight: 600;
}

.text-right {
    text-align: right;
}

.text-center {
    text-align: center;
}

.text-end {
    text-align: end;
}

.mt-4 {
    margin-top: 1.5rem;
}

.mb-2 {
    margin-bottom: 0.5rem;
}

.mb-3 {
    margin-bottom: 1rem;
}

.mb-4 {
    margin-bottom: 1.5rem;
}

img.logo {
    height: 60px;
    display: block;
    margin: 0 auto 10px auto;
}
